import React, { useState, useEffect } from "react";
import { Button } from "../form/Button";
import "../routing/Header.css";

function Header(props) {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  // reponsive function
  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener("resize", showButton);

  return (
    <>
      <nav className="header">
        <div className="header-container">
          <a href="" className="header-logo" onClick={closeMobileMenu}>
            GW <i class="far fa-university"></i>
          </a>

          <div className="menu-icon" onClick={handleClick}>
            <i className={click ? `fas fa-times` : `fas fa-bars`} />
          </div>

          <ul className={click ? "hea-menu active" : "hea-menu"}>
            <li className="hea-item">
              <a href="" className="hea-links" onClick={closeMobileMenu}>
              Create Account
              </a>
            </li>
            <li className="hea-item">
              <a
                href=""
                className="hea-links"
                onClick={closeMobileMenu}
              >
                Create Faculty
              </a>
            </li>
            <li className="hea-item">
              <a
                href=""
                className="hea-links"
                onClick={closeMobileMenu}
              >
                Persons
              </a>
            </li>
            <li className="hea-item">
              <a
                href=""
                className="hea-links"
                onClick={closeMobileMenu}
              >
                Faculty
              </a>
            </li>
            <li className="hea-item">
              <a
                href=""
                className="hea-links"
                onClick={closeMobileMenu}
              >
                Logout
              </a>
            </li>
          </ul>
        </div>
      </nav>
    </>
  );
}

export default Header;
