import { storage } from "../config/firebase";

let process;
export const handleUpload = (pdf) => {
  const uploadTask = storage.ref(`pdfs/${pdf.name}`).put(pdf);
  uploadTask.on(
    "state_changed",
    (snapshot) => {
      const progress = Math.round(
        (snapshot.bytesTransferred / snapshot.totalBytes) * 100
      );
      progress = process;
    },
    (error) => {
      alert("can not upload image", "error");
      console.log(error);
    },
    () => {
      storage
        .ref("pdfs")
        .child(pdf.name)
        .getDownloadURL()
        .then((url) => {
          alert("upload successfully", "success");
          return { process, url };
        });
    }
  );
};
