import { storage } from "../config/firebase";
import alert from '../constant/alert';
import moment from 'moment';
const SubmitReport = ({match}) => {
  const [pdf, setPdf] = useState(null);
  const [time, setTime] = useState();
  const [title, setTitle] = useState({
    title: "",
  });

  const handleChange = e => {
    if (e.target.files[0]) {
      setPdf(e.target.files[0]);
    }
  };
  function handle(e){
    const newData = {...title}
    newData[e.target.id]=e.target.value
    setTitle(newData)
  }
  const config = {
    headers: {
      "Content-Type": "application/json",
      token : localStorage.getItem("authToken")
    },
  };
  const handleUpload = (pdf) => {
    const uploadTask = storage.ref(`pdfs/${pdf.name}`).put(pdf);
    uploadTask.on(
      "state_changed",
      (snapshot) => {
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
      },
      (error) => {
        alert("can not upload pdf", "error");
        console.log(error);
      },
      () => {
         storage
          .ref("pdfs")
          .child(pdf.name)
          .getDownloadURL()
          .then(async (url, title) => {
            const {data} = await axios.post(
              `/homeStudent/lstFaculty/report/upload/${match.params.facultyID}`,
              { title: title, reportUrl:  url},
              config
            );
            if(data.success === true) {
            alert(data.message, "success");
            }
          });
      }
    );
  };

  useEffect(()=> {
    const  end = moment(match.params.endtime);
    const cur = moment();
    let checkTime = end.diff(cur, 'days');
    console.log(typeof checkTime);
    if(checkTime < 0) {
      checkTime = 0

    }
    setTime(checkTime)
  },[])
