import { storage } from "../config/firebase";

export const handleUpload = (pdf) => {
  const uploadTask = storage.ref(`pdfs/${pdf.name}`).put(pdf);
  uploadTask.on(
    "state_changed",
    (error) => {
      alert("can not upload image", "error");
      console.log(error);
    },
    () => {
      storage
        .ref("pdfs")
        .child(pdf.name)
        .getDownloadURL()
        .then((url) => {
          alert("upload successfully", "success");
          return url;
        });
    }
  );
};
